script:
frappe.ui.form.on('Purchase Receipt', {

	desity_per_sg: function(frm, cdt, cdn) 
            { 

                var d = locals[cdt][cdn];
               if(d.desity_per_sg && d.net_weight)
               {
                    d.qty_in_litters=flt(d.net_weight/d.desity_per_sg);
                    frm.refresh_fields();
                }
            } ,
            net_weight: function(frm, cdt, cdn) 
            { 

                var d = locals[cdt][cdn];
               if(d.desity_per_sg && d.net_weight)
               {
                    d.qty_in_litters=flt(d.net_weight/d.desity_per_sg);
                    frm.refresh_fields();
                }                

            } ,
});

frappe.ui.form.on("Purchase Receipt Item", 
    { 

            item_code: function(frm, cdt, cdn) 
            { 
                 var d = locals[cdt][cdn];


               if(d.item_group=="WASTE OIL")
               {
                   console.log("sb"+cur_frm.doc.qty_in_litters);
                    d.qty=cur_frm.doc.qty_in_litters;
                    frm.refresh_fields()
                }
            }, 
    }
);
